import React, { useEffect, useState } from 'react';
import { getBoardParts } from '../services/api';

const BoardParts = () => {
    const [boardParts, setBoardParts] = useState([]);

    useEffect(() => {
        getBoardParts().then(response => {
            setBoardParts(response.data);
        });
    }, []);

    return (
        <div>
            <h1>Board Parts</h1>
            <table style={styles.table}>
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Description</th>
                        <th>Model</th>
                        <th>Image</th>
                    </tr>
                </thead>
                <tbody>
                    {boardParts.map(boardPart => (
                        <tr key={boardPart._id}>
                            <td>{boardPart.name}</td>
                            <td>{boardPart.description}</td>
                            <td>{boardPart.model.name}</td>
                            <td>
                                {boardPart.image && <img src={`http://localhost:5000/${boardPart.image}`} alt={boardPart.name} style={styles.image} />}
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

const styles = {
    table: {
        width: '100%',
        borderCollapse: 'collapse',
    },
    image: {
        width: '50px',
        height: '50px',
    },
    th: {
        borderBottom: '2px solid #ddd',
        padding: '8px',
    },
    td: {
        borderBottom: '1px solid #ddd',
        padding: '8px',
    },
};

export default BoardParts;
