import React, { useEffect, useState } from 'react';
import { getBoardPartComponents } from '../services/api';

const BoardPartComponents = () => {
    const [combinations, setCombinations] = useState([]);

    useEffect(() => {
        getBoardPartComponents().then(response => {
            setCombinations(response.data);
        });
    }, []);

    return (
        <div>
            <h1>Board Part Components</h1>
            <table style={styles.table}>
                <thead>
                    <tr>
                        <th>Title</th>
                        <th>Board Part</th>
                        <th>Components</th>
                    </tr>
                </thead>
                <tbody>
                    {combinations.map(combination => (
                        <tr key={combination._id}>
                            <td>{combination.title}</td>
                            <td>{combination.boardPart.name}</td>
                            <td>
                                {combination.components.map(comp => (
                                    <div key={comp._id}>{comp.name} ({comp.type})</div>
                                ))}
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

const styles = {
    table: {
        width: '100%',
        borderCollapse: 'collapse',
    },
    th: {
        borderBottom: '2px solid #ddd',
        padding: '8px',
    },
    td: {
        borderBottom: '1px solid #ddd',
        padding: '8px',
    },
};

export default BoardPartComponents;
