import React from 'react';
import ComponentList from '../components/ComponentList';
import ComponentForm from '../components/ComponentForm';
import BulkUploadForm from '../components/BulkUploadForm';

const Components = () => {
    const [showForm, setShowForm] = React.useState(false);
    const [showBulkUpload, setShowBulkUpload] = React.useState(false);

    const toggleForm = () => {
        setShowForm(!showForm);
    };

    const toggleBulkUpload = () => {
        setShowBulkUpload(!showBulkUpload);
    };

    return (
        <div>
            <h1>Components</h1>
            <button onClick={toggleForm}>
                {showForm ? 'Hide Form' : 'Add New Component'}
            </button>
            <button onClick={toggleBulkUpload}>
                {showBulkUpload ? 'Hide Bulk Upload' : 'Bulk Upload Components'}
            </button>
            {showForm && <ComponentForm />}
            {showBulkUpload && <BulkUploadForm />}
            <ComponentList />
        </div>
    );
};

export default Components;
